sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var ga = new GlideAjax('email_PK_test2');
	ga.addParam('sysparm_name','isEmailAlreadyPresent');
	ga.addParam('sysparm_emailValue',newValue);
	ga.getXML(parseResponseXML);
	//alert(ga.getXMLAnswer());

	function parseResponseXML(response) {
		var resp = response.responseXML.documentElement.getAttribute("answer");
		//alert('result= ' +resp);
		if(resp === 'true') {
			g_form.setValue('email', '');
			g_form.addInfoMessage('Email address already used.');
		} else {
			g_form.clearMessages();
		}

			return;
		}
	}
