sys_script_include.script:
var email_PK_test2 = Class.create();
email_PK_test2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	type: 'email_PK_test2',
	isEmailAlreadyPresent: function() {
		var emailValue = this.getParameter('sysparm_emailValue');
		var result = false;
		var db = new GlideRecord('x_195734_peopleman_people');
		db.addQuery('email','=',emailValue);
		db.query();

		if(db.next()) {
			result = true;
		}
		gs.warn(result);
		return result;

	}
});
